nta.declaration:
/** Global declarations
 *
 * COPYRIGHT (c) 2018 Russell Maguire
 */

// Constants.
const int ADDR_WIDTH = 4;
const int LINE_WIDTH = 1;
const int TAG_WIDTH = ADDR_WIDTH - LINE_WIDTH;

const int LINE_OFFSET = 0;
const int TAG_OFFSET = LINE_OFFSET + LINE_WIDTH;

const int ADDR_MAX = (1 << ADDR_WIDTH) - 1;
const int LINE_MAX = (1 << LINE_WIDTH) - 1;

const bool READ = true;
const bool WRITE = false;

// Global clock.
clock time;

template.declaration:
/** Core process template
 *
 * DESCRIPTION
 *    Template for process which generates randomly generates read and write requests
 *
 * INPUTS
 *    addr     - address bus array of boolean values
 *    rw       - flag indicicating RW_WRITE or RW_READ
 *    request  - channel signaling memory request
 *    response - channel signaling memory response
 *
 * COPYRIGHT (c) 2018 Russell Maguire
 */

clock t;

void update_addr(const int[0,ADDR_MAX] value)
{
    for (i : int[0,ADDR_WIDTH-1])
    {
        addr[i] = (value >> i) & 1;
    }
}

void update_readwrite(const bool value)
{
    readwrite = value;
}

// MemController declarations
// COPYRIGHT (c) 2018 Russell Maguire

const int TMIN = 25;
const int TMAX = 30;

clock t;

/**
 */

const int TMINR = 5;
const int TMAXR = 10;
const int TMINW = 25;
const int TMAXW = 30;

bool line[LINE_WIDTH];
bool tag[TAG_WIDTH];
bool valid = false;

// Local clock for timed cache accesses.
clock t;

void init_cache_line()
{
    for (i : int[0,LINE_WIDTH-1])
    {
        line[i] = (id >> (LINE_OFFSET+i)) & 1;
    }
}

/** Process-local check for cache line.
 */
bool is_cache_line()
{
    // Cache line if all line bits match.
    return forall (i : int[0,LINE_WIDTH-1]) addr[LINE_OFFSET+i] == line[i];
}

/** Process-local check for cache hit.
 */
bool is_cache_hit()
{
    // Cache hit if cache line is valid and all tag bits match.
    return valid and forall (i : int[0,TAG_WIDTH-1]) addr[TAG_OFFSET+i] == tag[i];
}

/** Process-local procedure to update cache tag and valid bits.
 */
void update_cache_line()
{
    // Only model tag, don't bother writing data.
    for (i : int[0,TAG_WIDTH-1])
    {
        tag[i] = addr[TAG_OFFSET+i];
    }

    // Set cache valid bit.
    valid = true;
}

/**
 */

int cache_hits = 0;
int cache_misses = 0;

/** Less than
 */
bool hit_rate_lt(const int hit_rate)
{
    return cache_hits + cache_misses > 0
        && (100 - hit_rate) * cache_hits < hit_rate * cache_misses;
}

/** Less than or equal to
 */
bool hit_rate_le(const int hit_rate)
{
    return cache_hits + cache_misses > 0
        && (100 - hit_rate) * cache_hits <= hit_rate * cache_misses;
}

/** Equal to
 */
bool hit_rate_eq(const int hit_rate)
{
    return cache_hits + cache_misses > 0
        && (100 - hit_rate) * cache_hits == hit_rate * cache_misses;
}

/** Greater than or equal to
 */
bool hit_rate_ge(const int hit_rate)
{
    return cache_hits + cache_misses > 0
        && (100 - hit_rate) * cache_hits >= hit_rate * cache_misses;
}

/** Greater than or equal to
 */
bool hit_rate_gt(const int hit_rate)
{
    return cache_hits + cache_misses > 0
        && (100 - hit_rate) * cache_hits > hit_rate * cache_misses;
}

/** Not equal to
 */
bool hit_rate_ne(const int hit_rate)
{
    return cache_hits + cache_misses > 0
        && (100 - hit_rate) * cache_hits != hit_rate * cache_misses;
}

nta.system:
/** Main memory system
 * COPYRIGHT (c) 2018 Russell Maguire
 */

// Address bus.
bool addr[ADDR_WIDTH];
bool readwrite;

// Channels
broadcast chan cache_hit;
broadcast chan cache_miss;
broadcast chan cache_request;
chan cache_response;
chan memory_request;
chan memory_response;

// System processes
core = Core(addr, readwrite, cache_request, cache_response);
memory = Memory(memory_request, memory_response);
cache_line(const int[0,LINE_MAX] id) = CacheLine(id, addr, readwrite, cache_request, cache_response, memory_request, memory_response, cache_hit, cache_miss);
cache_counter = CacheCounter(cache_hit, cache_miss);

system core, memory, cache_line, cache_counter;
